import React, { Component } from 'react'
import Plan from './Plan'
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import axios from 'axios'

class App extends Component {
  state= {
    items: [],
    text: ""
  }
  showPlan = () => {
    axios.get('http://127.0.0.1:8000/list/')
    .then((res)=>{
      this.setState({ items: res.data })
    })
  }
  addPlan = () => {
    if (this.state.text !==""){
      axios.post('http://127.0.0.1:8000/create/')
      .then((res)=>{
        this.setState({ text: '' })
      })
    }
  }
  handleChange = e =>{
    this.setState({ text: e.target.value })
  }
  handleAdd = e =>{
    if (this.state.text !==""){
      const items = [...this.state.items, this.state.text]
      this.setState({ items: items, text: "" })
    }
  }
  handleDelete = id => {
    const Olditems = [...this.state.items]
    console.log("Olditems", Olditems);
    const items = Olditems.filter((element, i) => {
      return i !== id
    }) 
    this.setState({items:items});
  }
  componentDidMount(){
    this.showPlan();
  }
  render() {
    return (
      <div className="container-fluid my-5">
        <div className="row">
          <div className="col-sm-6 mx-auto text-white shadow-lg p-3">
            <h2 className="text-center"> Today's Plan </h2>
            <div className="col-sm-12 mx-auto">
              <input type="text" className="form-control" placeholder="write your text" value={this.state.text} onChange={this.handleChange} />
            </div>
            <div className="col-2 mx-auto">
              <button className="btn btn-warning px-5 fw-bold my-2" onClick={this.handleAdd}>Add</button>
            </div>
            <div className="container-field">
              <ul class="list-unstyled row m-5">
                {
                this.state.items.map((value, i)=>{
                  return <Plan key={i} id={value.i} value={value.item} sendData ={this.handleDelete} />
                })
                }
              </ul>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
export default App;
